<?php $sendTo='chiki.monk3y@gmail.com';$okMessage='Contact form successfully submitted. We will get back to you soon!';$errorMessage='There was an error while submitting the form. Please try again later.';error_reporting(E_ALL&~E_NOTICE);try{if(count($_POST)==0&&!isset($_POST['name'])&&!isset($_POST['email'])&&!isset($_POST['message'])){throw new \Exception('Form is empty');}$from=$_POST['email'];$message=$_POST['message'];$subject=$_POST['name'];$headers='From: '.$from."\r\n".'Reply-To: '.$sendTo."\r\n".'X-Mailer: PHP/'.phpversion();mail($sendTo,$subject,$message,$headers);$responseArray=array('type'=>'success','message'=>$okMessage);}catch(\Exception $e){$responseArray=array('type'=>'danger','message'=>$errorMessage);}if(!empty($_SERVER['HTTP_X_REQUESTED_WITH'])&&strtolower($_SERVER['HTTP_X_REQUESTED_WITH'])=='xmlhttprequest'){$encoded=json_encode(array('status'=>true,'message'=>$okMessage));header('Content-Type: application/json');echo $encoded;}else{$encoded=json_encode(array('status'=>false,'message'=>$errorMessage));header('Content-Type: application/json');echo $encoded;}